mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT meeting_no, meeting_master_id, meeting_center, meeting_name, title_img, meeting_detail, meeting_rule, interest_name, snooze, meeting_views, meeting_condition, shut_down_date, meeting_crew_limit FROM meeting
meeting_master_id = #{userId} AND meeting_condition != 'DEL'
) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT meeting_no, meeting_master_id, meeting_center, meeting_name, title_img, meeting_detail, meeting_rule, interest_name, snooze, meeting_views, meeting_condition, shut_down_date, meeting_crew_limit FROM meeting
meeting_master_id = #{value} AND meeting_condition != 'DEL'
) countTable

mapper.update:
UPDATE meeting_crew_list

	crew_condition = #{crewCondition}


	crew_id = #{meetingMasterId} 
	AND meeting_no = #{meetingNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT act_crew_no as meeting_no, meeting_act_no, meeting_crew_no, meeting_act_count, meeting_act_date as meeting_date, meeting_act_time as meeting_time, meeting_act_location as meeting_location FROM meeting_act_crew_list
meeting_act_no = #{meetingNo} and act_crew_id= #{userId}
ORDER BY meeting_act_count ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT act_date as meeting_date, meeting_act_count, act_location as meeting_location, meeting_act_no, act_time as meeting_time
from meeting_act

	meeting_no = #{meetingNo}

  	SELECT COUNT(*)
  	FROM(	SELECT act_date as meeting_date, meeting_act_count, act_location as meeting_location, meeting_act_no, act_time as meeting_time
				 	from meeting_act

				 		meeting_no = #{value}

					 ) countTable

SELECT act_crew_id as meeting_master_id
FROM meeting_act_crew_list

	meeting_act_no = #{meetingActNo} and meeting_act_count = #{meetingActCount}

SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT m.meeting_no, m.meeting_master_id, m.meeting_center, m.meeting_name, m.title_img, m.meeting_detail, m.meeting_rule, m.interest_name, m.snooze, m.meeting_views, m.meeting_condition, m.shut_down_date, m.meeting_crew_limit FROM meeting m, meeting_crew_list mcl
m.meeting_no = mcl.meeting_no AND mcl.crew_id IN (#{userId}) AND meeting_condition != 'DEL'
) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT m.meeting_no, m.meeting_master_id, m.meeting_center, m.meeting_name, m.title_img, m.meeting_detail, m.meeting_rule, m.interest_name, m.snooze, m.meeting_views, m.meeting_condition, m.shut_down_date, m.meeting_crew_limit FROM meeting m, meeting_crew_list mcl
m.meeting_no = mcl.meeting_no AND mcl.crew_id IN (#{userId}) AND meeting_condition != 'DEL'
) countTable

  	SELECT COUNT(*)
  	FROM(	SELECT comu.community_no, comu.community_writer_id, comu.category, comu.community_user_grade, comu.community_title, comu.community_writer_nick_name, comu.community_detail, comu.community_write_date, comu.community_views, comu.community_like, comu.import_rank, comu.community_view_condition, up.file_name
					FROM community comu, upload up

						comu.community_writer_id = #{userId}
						and comu.community_no = up.community_no 

					 ) countTable

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT comment_no , community_no , comment_writer_id , comment_detail , comment_write_date, comment_target_no, comment_view_condition
										FROM comments
										WHERE comment_writer_id = #{userId} 
										ORDER BY comment_target_no, comment_no
								 	) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT comment_no , community_no , comment_writer_id , comment_detail , comment_write_date, comment_target_no, comment_view_condition
					FROM comments
					WHERE comment_writer_id = #{userId}
				 ) countTable

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT chatting_no , man_id , woman_id , chatting_date , contact_meeting
										FROM chatting

											man_id = #{userId} or woman_id = #{userId}

								 	) inner_table
					WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT chatting_no , man_id , woman_id , chatting_date , contact_meeting
		FROM chatting

			man_id = #{userId} or woman_id = #{userId}

		) countTable
